const client = require('@sendgrid/client');

module.exports = {    
    execute: function(event, ctx, config) { 
        client.setApiKey(config.SENDGRID_API_KEY);
        const data = {
            "list_ids":[config.LIST_ID],
            "contacts": [
                {
                "email": event.mail_to  
                }
            ]
        };
        const request = {
            url: `/v3/marketing/contacts`,
            method: 'PUT',
            body: data
        }

        const promise = client.request(request)
        promise.then(([response, body]) => {
                console.log(response.statusCode);
                console.log(response.body);
            })
        .catch(error => {
            console.error(error);
        });

        return promise;
    }
}
