const AWS = require('aws-sdk');
const { PassThrough } = require('stream');

let writeStream, uploadPromise;
module.exports = {
  setup: async function(config) { /* optional */
    AWS.config.update({
      accessKeyId: config.ACCESS_KEY_ID,
      secretAccessKey: config.SECRET_ACCESS_KEY,
      region: config.REGION
    });

    const s3 = new AWS.S3();
    writeStream = new PassThrough();

    const uploadOptions = {
      Bucket: config.BUCKET_NAME,
      Key: `${new Date().toISOString()}.json`,
      Body: writeStream
    };

    uploadPromise = s3.upload(uploadOptions).promise();
  },
  execute: function(event, ctx, config) { /* required */
    writeStream.write(JSON.stringify(event) + '\n', 'utf8');
  },
  teardown: function(config) {  
    writeStream.end(); 
    return uploadPromise;
  }
};
