function stripSSN(obj) {
    if (typeof obj !== 'object' || obj === null) {
      return obj;
    }

    if (Array.isArray(obj)) {
      return obj.map((item) => stripSSN(item));
    }

    const strippedObj = {};

    for (const [key, value] of Object.entries(obj)) {
      if (typeof value === 'string') {
        strippedObj[key] = value.replace(/ d{3}-d{2}-d{4} /g, 'XXX-XX-XXXX');
      } else {
        strippedObj[key] = stripSSN(value);
      }
    }

    return strippedObj;
  }

  module.exports = function (event, ctx) {    
      event = stripSSN(event);
      return event;
  }
