const axios = require('axios');

module.exports = {    
    execute: async function(event, ctx, config) {
        try {
            const response = await axios.post(`${config.INSTANCE_URL}/api/now/table/incident`, event, {
                auth: {
                    username: config.USERNAME,
                    password: config.PASSWORD
                },
                headers: {
                    'Content-Type': 'application/json'
                }
            });
            console.log(`Incident created: ${response.data.result.number}`);
        } catch(err) {
            console.error("Error while creating incident", err);
            throw err;
        }
    }
}
