const snowflake = require('snowflake-sdk');

let connection;

module.exports = {
    setup: function(config) { /* optional */        
        const connectionConfig = {
            account: config.account,
            username: config.username,
            password: config.password,
            warehouse: config.warehouse,
            database: config.database,
            schema: config.schema,
        };

        return new Promise ((resolve, reject) => {
            connection = snowflake.createConnection(connectionConfig);
            connection.connect(function(err, conn) {
                if (err) {
                    console.error('Unable to connect: ' + err.message);
                    reject(err);
                } 
                else {
                    console.log('Successfully connected to Snowflake.');
                    resolve();
                }
            });
        });

    },
    execute: async function(event, ctx, config) { /* required */

        return new Promise ((resolve, reject) => {
            connection.execute({
                sqlText: 'INSERT INTO CPTN_EVENT (EVENT_TYPE, IP_ADDR) VALUES (?, ?)',
                binds: [event.type, event.ip],
                complete: function(err, stmt, rows) {
                    if (err) {
                        console.error('Failed to execute statement due to the following error: ' + err.message);
                        reject(err);
                    } else {
                        console.log('Successfully executed statement: ' + stmt.getSqlText());
                        resolve();
                    }
                }
            });
        });

    },
    teardown: function(config) { /* optional */

        return new Promise ((resolve, reject) => {
            connection.destroy(function(err, conn) {
                if (err) {
                    console.error('Unable to disconnect: ' + err.message);
                    reject(err);
                } else {
                    console.log('Disconnected connection with id: ' + connection.getId());
                    resolve();
                }
            });
        });

    }
}
